import { ShoppingCartOutlined } from "@ant-design/icons";
import { Button, Card, message, Spin, InputNumber } from "antd";
import React, { useState, useEffect } from "react";
import { config } from "../App";
import "./Cart.css";

/**
 * @typedef {Object} Product
 * @property {string} name - The name or title of the product
 * @property {string} category - The category that the product belongs to
 * @property {number} cost - The price to buy the product
 * @property {number} rating - The aggregate rating of the product (integer out of five)
 * @property {string} image - Contains URL for the product image
 * @property {string} _id - Unique ID for the product
 */

/**
 * @typedef {Object} CartItem
 * @property {string} productId - Unique ID for the product
 * @property {number} qty - Quantity of the product in cart
 * @property {Product} product - Corresponding product object for that cart item
 */

/**
 * @function Cart component handles functionality for the display and manipulation of the customer's shopping cart
 *
 * Contains the following fields
 * @property {Product[]} props.products
 *    List of all available products (that the cart items can be from)
 * @property {{ push: function }} props.history
 *    To navigate and redirect the user to different routes or pages
 * @property {string} props.token
 *    Oauth token for authentication for API calls
 * @property {boolean|undefined} props.checkout
 *    Denotes if the Cart component is created in the Checkout component
 */

const Cart = ({ products, history, token, checkout }) => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);

  const validateResponse = (errored, response) => {
    if (errored) {
      message.error(
        "Could not update cart. Check that the backend is running, reachable and returns valid JSON."
      );
      return false;
    } else if (response.message) {
      message.error(response.message);
      return false;
    }

    return true;
  };

  const getCart = async () => {
    let response = {};
    let errored = false;

    setLoading(true);

    console.log({token})

    try {
      response = await (
        await fetch(`${config.endpoint}/cart`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
      ).json();
    } catch (e) {
      errored = true;
    }

    setLoading(false);

    if (validateResponse(errored, response)) {
      return response;
    }
  };

  const postToCart = async (productId, qty) => {
    let response = {};
    let errored = false;
    let statusCode;

    setLoading(true);

    try {
      response = await (
        await fetch(`${config.endpoint}/cart`, {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            productId: productId,
            quantity: qty,
          }),
        })
      ).json();
    } catch (e) {
      errored = true;
    }

    setLoading(false);

    if (validateResponse(errored, response, statusCode)) {
      await refreshCart();
    }
  };

  const putToCart = async (productId, qty) => {
    let response = {};
    let errored = false;
    let statusCode;

    setLoading(true);

    try {
      let response_object = await fetch(`${config.endpoint}/cart`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          productId: productId,
          quantity: qty,
        }),
      });

      statusCode = response_object.status;
      if (statusCode !== 204) {
        response = await response_object.json();
      }
    } catch (e) {
      errored = true;
    }

    setLoading(false);
    if (
      statusCode === "204" ||
      validateResponse(errored, response, statusCode)
    ) {
      await refreshCart();
    }
  };

  const refreshCart = async () => {
    const cart = await Promise.resolve[getCart()];
    if (cart && cart.cartItems) {
      setItems(
        cart.cartItems.map((item) => ({
          ...item,
          product: products.find((product) => product._id === item.productId),
        }))
      );
    }

    if (checkout && items.length === 0) {
      message.error("Cart is empty. Please add items to the cart first");
      history.push("/");
    }
  };

  useEffect(() => {
    refreshCart();
  }, []);

  return (
    <div className="cart">
      {items.length ? (
        <div className="cart-items">
          {items.map((item) => (
            <Card className="cart-item" key={item.productId}>
              <img
                className="cart-item-image"
                alt={item.product.name}
                src={item.product.image}
              />
              <div className="cart-item-info">
                <div className="cart-item-title">{item.product.name}</div>
                <div className="cart-item-category">
                  Category: {item.product.category}
                </div>
                <div className="cart-item-price">₹{item.product.cost}</div>
              </div>
              <div className="cart-item-actions">
                {isReadOnly ? <div>{item.qty}</div>:<InputNumber
                  min={1}
                  max={10}
                  defaultValue={item.qty}
                  onChange={(value) =>
                    putToCart(item.productId, value).catch((e) =>
                      message.error("Could not update cart.")
                    )
                  }
                />}
              </div>
            </Card>
          ))}
        </div>
      ) : (
        <div className="empty-cart">
          <ShoppingCartOutlined className="empty-cart-icon" />
          <h3>Cart is empty.</h3>
          <Button
            type="primary"
            onClick={() => history.push("/")}
            loading={loading}
          >
            Continue Shopping
          </Button>
        </div>
      )}
      {checkout && (
        <div className="cart-footer">
          <div className="cart-total">
            Total: ₹
            {items.reduce((total, item) => total + item.qty * item.product.cost, 0)}
          </div>
          <Button
            type="primary"
            onClick={() => message.success("Order placed successfully")}
            loading={loading}
          >
            Place Order
          </Button>
        </div>
      )}
    </div>
  );
};

export default Cart;
